def get_funda_vals(ticker):
    fundavals.clear() #clear the list so that it does not contain repeated text 
    url = f'https://www.screener.in/company/{ticker}/consolidated/'
    res = requests.get(url)
    res = res.text
    soup = BeautifulSoup(res, 'lxml')
    funda = soup.find_all('span', class_='number')
    for each in funda:
            val = each.text
            val = val.replace('<span class="number">','')
            val = val.replace('</span>','')
            fundavals.append(val)
    marktcap = (fundavals[0])
    high_price = (fundavals[2])
    low_price = (fundavals[3])
    pe_ratio = (fundavals[4])
    book_price = (fundavals[5])
    div_yeild = (fundavals[6])
    roce = (fundavals[7])
    roe = (fundavals[8])
    faceval = (fundavals[9])
    df = ({'Market Cap' : marktcap, 'High Price' :high_price, 'Low Price' :low_price, 'PE Ratio' : pe_ratio, 'Book Price' : book_price, 'Dividend Yeild' : div_yeild, 'ROCE' : roce, 'ROE': roe, 'Face Value' : faceval  })
    return df



def handle(msg):
    content_type, chat_type, chat_id = telepot.glance(msg)
    print(content_type, chat_type, chat_id)

    if content_type == 'text':
        if msg['text'] in ticker_lis:
            deatils = get_funda_vals(msg['text'])
            bot.sendMessage(chat_id, f"Fundamentals for the ticker {msg['text']} are:")
            bot.sendMessage(chat_id, f"Market Cap : {deatils['Market Cap']}")
            bot.sendMessage(chat_id, f"High Price : {deatils['High Price']}")
